# -*- coding: utf-8 -*-# Feature : mouvement de la tetefrom NaoApplication import *from naoqi import ALProxyfrom naoqi import ALBrokerfrom naoqi import ALModuleclass Head(Features):    def __init__(self, robotProxy):        self.name = "Head"        self.bot = robotProxy              def run(self, robotIP, feature):        # Instanciation proxy pour les mouvements de la tete        motionProxy = self.bot.gMotionProxy                          # Instanciation proxy pour la posture initiale         postureProxy = self.bot.gPostureProxy                        # Recuperation des angles envoye par le main        degreHori = 0        degreVerti = 0        degreHori = ((feature["head"]["yaw"]*0.3045*3.14)/180) # en radian max |1.8|        degreVerti = ((feature["head"]["pitch"]*0.238*3.14)/180) # en radian max entre -0.4 et 0.55        # Reglage proxy pour le mouvement de la tete        motionProxy.setStiffnesses("Head", 1.0)                # Reglage proxy pour le mouvement horizontal de la tete        names = "HeadYaw"        angles = degreHori        times = 1.5        isAbsolute = False        motionProxy.post.angleInterpolation(names, angles, times, isAbsolute)        # Reglage proxy pour le mouvement vertical de la tete         names = "HeadPitch"        angles = degreVerti        times = 1.5        isAbsolute = False        motionProxy.post.angleInterpolation(names, angles, times, isAbsolute)        print("j'ai bouge la tete")     