# -*- coding: utf-8 -*-# Feature : mise en posture de reposfrom NaoApplication import *from naoqi import ALProxyfrom naoqi import ALBrokerfrom naoqi import ALModuleclass Rest(Features):    def __init__(self, robotProxy):        self.name = "Rest"        self.bot = robotProxy              def run(self, robotIP, feature):        # Instanciation proxy pour les mouvements des bras lors de la marche        motionProxy = self.bot.gMotionProxy           # Instanciation proxy pour la posture accroupi         postureProxy = self.bot.gPostureProxy        # Posture accroupi        postureProxy.goToPosture("Sit", 0.5)        # desasservissement des moteurs pour eviter la chauffe        pNames = "Body"        pStiffnessLists = 0.0        pTimeLists = 1.0        motionProxy.stiffnessInterpolation(pNames, pStiffnessLists, pTimeLists)        